            currentState: State.ReadyForBids,
            PU: _PU,
            bandwidth: _bandwidth,
            minBidValue: _minBidValue * 1 wei,
            depositValue: _depositValue * 1 wei,
            BidsDeadline: block.timestamp + 1 days,
